import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    try {
      const response = await axios.post('https://your-api.com/auth/login', {
        username,
        password
      });

      const data = response.data;
      try {
        if (typeof localStorage === 'undefined') {
          console.error('localStorage is not available in this environment.');
          return;
        }

        const token = data?.token;
        if (!token || typeof token !== 'string') {
          console.error('Invalid token received from server.');
          return;
        }

        // In production, consider storing this in an httpOnly cookie instead
        localStorage.setItem('auth_token', token);
        console.log('Logged in successfully');
        navigate('/tasks');
      } catch (storageError) {
        console.error(
          'Failed to store token in localStorage:',
          storageError instanceof Error ? storageError.message : storageError
        );
      }
      // redirect or navigate here
    } catch (err) {
      console.error('Login failed:', err);
      // show error to user
    }
  };

  return (
    <div className="container py-5">
      <h2 className="text-center mb-4">Login</h2>
      <form onSubmit={handleSubmit} className="card p-4 shadow-sm mx-auto" style={{ maxWidth: '400px' }}>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <input
            type="password"
            className="form-control"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary w-100">
          Log In
        </button>
      </form>
    </div>
  );
};

export default Login;
